import com.squareup.moshi.Types
import com.squareup.moshi.kotlin.reflect.KotlinJsonAdapterFactory
import okio.Buffer
import org.junit.Assert
import org.junit.Before
import org.junit.Test

class LiveTimingDataParserTest {
    private lateinit var moshi: Moshi

    @Before
    fun setUpMoshi() {
        moshi = Moshi.Builder()
            .add(DateParser())
            .add(LiveTimingListParser.Factory)
            .add(PreviousDataParser.Factory)
            .add(LiveTimingDataParser.Factory)
            .add(KotlinJsonAdapterFactory()).build()
    }

    @Test
    fun jsonToLiveTimingStats() {
        // Given json string containing LiveTimingData add proper adapter
        val jsonStr =
            "{\"H\":\"Streaming\",\"M\":\"feed\", \"A\":[\"TimingStats\", {\"Lines\": {\"7\": {\"BestSpeeds\": {\"I1\": {\"Position\": 2}}}, \"14\": {\"BestSpeeds\": {\"I1\": {\"Position\": 3}}}}}, \"2021-10-08T08:30:51.596Z\"]}"
        val jsonReader = JsonReader.of(Buffer().writeUtf8(jsonStr))
        val adapter = moshi.adapter(LiveTimingData::class.java)

        // When parsing data using moshi custom adapter
        val liveTimingData: LiveTimingData.LiveTimingStatsDto = adapter.fromJson(jsonReader) as LiveTimingData.LiveTimingStatsDto

        Assert.assertEquals(Constants.LiveTimingDataType.TIMING_STATS, liveTimingData.type)
        Assert.assertEquals(8, liveTimingData.date!!.dayOfMonth)
        Assert.assertEquals(51, liveTimingData.date!!.second)
        Assert.assertEquals(30, liveTimingData.date!!.minute)
        Assert.assertEquals(2, liveTimingData.data!!.lines[7]!!.bestSpeeds!!["I1"]!!.position,)
    }

    @Test
    fun jsonToLiveTimingAppData() {
        // Given json string containing LiveTimingData add proper adapter
        val jsonStr =
            "{\"H\":\"Streaming\",\"M\":\"feed\", \"A\":[\"TimingAppData\", {\"Lines\":{\"3\":{\"Stints\":{\"1\":{\"TotalLaps\":14}}},\"11\":{\"Stints\":{\"1\":{\"LapFlags\":0,\"Compound\":\"MEDIUM\",\"New\":\"true\",\"TyresNotChanged\":\"0\",\"TotalLaps\":0,\"StartLaps\":0}}},\"33\":{\"Stints\":{\"1\":{\"TotalLaps\":10}}},\"55\":{\"Stints\":{\"1\":{\"TotalLaps\":22}}}}}, \"2021-10-08T08:34:53.943Z\"]}"
        val jsonReader = JsonReader.of(Buffer().writeUtf8(jsonStr))
        val adapter = moshi.adapter(LiveTimingData::class.java)

        // When parsing data using the moshi custom adapter
        val liveTimingData: LiveTimingData.LiveTimingAppDataDto = adapter.fromJson(jsonReader) as LiveTimingData.LiveTimingAppDataDto

        // Then parse data is correct
        Assert.assertEquals(Constants.LiveTimingDataType.TIMING_APP_DATA, liveTimingData.type)
        Assert.assertEquals("2021-10-08T08:34:53.943", liveTimingData.date.toString())
        Assert.assertEquals("MEDIUM", liveTimingData.data!!.lapInfo[11]!!.stints[1]!!.compound)
    }

    @Test
    fun jsonToLiveTimingData() {
        // Given json string containing LiveTimingData add proper adapter
        val jsonStr =
            "{\"H\":\"Streaming\",\"M\":\"feed\", \"A\":[\"TimingData\", {\"Lines\": {\"3\": {\"Line\": 15, \"Position\": \"15\"}, \"4\": {\"GapToLeader\": \"+5.804\", \"Line\": 10, \"Position\": \"10\", \"Speeds\": {\"ST\": {\"Value\": \"321\", \"PersonalFastest\": True}}}, \"5\": {\"TimeDiffToFastest\": \"+1.483\", \"Line\": 5, \"Position\": \"5\"}, \"7\": {\"Line\": 14, \"Position\": \"14\"}, \"9\": {\"TimeDiffToFastest\": \"+6.583\", \"Line\": 11, \"Position\": \"11\"}, \"10\": {\"TimeDiffToFastest\": \"+10.859\", \"Line\": 12, \"Position\": \"12\", \"Sectors\": {\"1\": {\"Value\": \"31.099\", \"PersonalFastest\": True}}, \"Speeds\": {\"I2\": {\"Value\": \"281\", \"PersonalFastest\": True}}}, \"14\": {\"Line\": 13, \"Position\": \"13\"}, \"16\": {\"TimeDiffToFastest\": \"+0.018\", \"TimeDiffToPositionAhead\": \"+0.018\", \"Line\": 2, \"Position\": \"2\", \"LastLapTime\": {\"OverallFastest\": False}}, \"18\": {\"TimeDiffToFastest\": \"+1.223\", \"Line\": 4, \"Position\": \"4\"}, \"22\": {\"TimeDiffToFastest\": \"+3.000\", \"Line\": 9, \"Position\": \"9\"}, \"31\": {\"TimeDiffToFastest\": \"+1.732\", \"Line\": 6, \"Position\": \"6\"}, \"47\": {\"TimeDiffToFastest\": \"+2.594\", \"Line\": 8, \"Position\": \"8\"}, \"77\": {\"TimeDiffToFastest\": \"+0.565\", \"Line\": 3, \"Position\": \"3\"}, \"99\": {\"TimeDiffToFastest\": \"+1.958\", \"Line\": 7, \"Position\": \"7\"}}}, \"2021-10-08T08:34:53.943Z\"]}"
        val jsonReader = JsonReader.of(Buffer().writeUtf8(jsonStr))
        val adapter = moshi.adapter(LiveTimingData::class.java)

        // When parsing data using the moshi custom adapter
        val liveTimingData: LiveTimingData.LiveTimingDataDto = adapter.fromJson(jsonReader) as LiveTimingData.LiveTimingDataDto
        // Then parse data is correct
        Assert.assertEquals(Constants.LiveTimingDataType.TIMING_DATA, liveTimingData.type)
        Assert.assertEquals(8, liveTimingData.date!!.dayOfMonth)
        Assert.assertEquals(53, liveTimingData.date!!.second)
        Assert.assertEquals(34, liveTimingData.date!!.minute)
        Assert.assertEquals("+5.804", liveTimingData.data!!.lines[4]!!.gap)
        Assert.assertEquals(null, liveTimingData.data!!.lines[4]!!.qualyfingStats)
    }

    @Test
    fun jsonToLiveCarData() {
        // Given json string containing LiveTimingData add proper adapter
        val jsonStr =
            "{\"H\":\"Streaming\",\"M\":\"feed\", \"A\":[\"CarData.z\", \"\", \"2021-10-08T08:34:53.943Z\"]}"
        val jsonReader = JsonReader.of(Buffer().writeUtf8(jsonStr))
        val adapter = moshi.adapter(LiveTimingData::class.java)

        // When parsing data using the moshi custom adapter
        val liveTimingData: LiveTimingData.LiveTimingBulkMessage = adapter.fromJson(jsonReader) as LiveTimingData.LiveTimingBulkMessage
        val carData = moshi.adapter(CarDataDto::class.java).fromJson(LiveTimingUtils.decodeMessage(liveTimingData.data))

        // Then parse data is correct
        Assert.assertEquals(Constants.LiveTimingDataType.CAR_DATA, liveTimingData.type)
        Assert.assertEquals("2021-10-08T08:34:53.943", liveTimingData.date.toString())
        Assert.assertEquals(8887, carData!!.entries[2].cars[7]!!.telemetry.rpmValue)
    }

    @Test
    fun jsonToLivePositionData() {
        // Given json string containing LiveTimingData add proper adapter
        val jsonStr =
            "{\"H\":\"Streaming\",\"M\":\"feed\", \"A\":[\"Position.z\", \"\", \"2021-10-08T08:34:53.943Z\"]}"
        val jsonReader = JsonReader.of(Buffer().writeUtf8(jsonStr))
        val adapter = moshi.adapter(LiveTimingData::class.java)

        // When parsing data using the moshi custom adapter
        val liveTimingData: LiveTimingData.LiveTimingBulkMessage =
            adapter.fromJson(jsonReader)!! as LiveTimingData.LiveTimingBulkMessage
        val positionData = moshi.adapter(PositionDataDto::class.java).fromJson(LiveTimingUtils.decodeMessage(liveTimingData.data))

        // Then parse data is correct
        Assert.assertEquals(Constants.LiveTimingDataType.POSITION_DATA, liveTimingData.type)
        Assert.assertEquals("2021-10-08T08:34:53.943", liveTimingData.date.toString())
        Assert.assertEquals(-14077F, positionData!!.entries[3].cars[31]!!.xPosition)
    }

